  width?: string | number;

  /**
   * Map container height
   */
  height?: string | number;

  /**
   * Map container style
   */
  style?: CSSProperties;

  /**
   * Map container className
   */
  className?: string;
}

export class Map extends React.Component<
  MapProps & WithYMapsProps & WithInstanceRef & ErrorBoundaryProps
> {
  constructor() {
    super();
    this.instance = null;
    this.state = { instance: null };
    this._parentElement = null;
    this._getRef = (ref) => {
      this._parentElement = ref;
    };
  }

  componentDidMount() {
    this.instance = Map.mountObject(
      this._parentElement,
      // eslint-disable-next-line react/prop-types
      this.props.ymaps.Map,
      this.props
    );

    this.setState({ instance: this.instance });
  }

  componentDidUpdate(prevProps) {
    if (this.instance !== null) {
      Map.updateObject(this.instance, prevProps, this.props);
